mapper.select:
SELECT *
FROM t_manual_incubation
WHERE base_id = #{baseId}
  AND dovecote_number = #{dovecoteNumber}
  AND labor_time = #{date}
    AND is_deleted = 0

SELECT *
FROM t_manual_incubation
WHERE base_id = #{baseId}
  AND dovecote_number = #{dovecoteNumber}
  AND is_deleted = 0

SELECT *
FROM t_manual_incubation
WHERE base_id = #{baseId}
    AND year(gmt_create) = #{year}
  AND month(gmt_create) = #{month}
  AND day(gmt_create) = #{day}
  AND is_deleted = 0

SELECT *
FROM t_manual_incubation
WHERE base_id = #{baseId}
  AND dovecote_number = #{dovecoteNumber}
  AND gmt_create >= DATE_SUB(NOW(),INTERVAL 7 DAY )
AND is_deleted = 0
