mapper.select:
SELECT

FROM t_cage_real r LEFT JOIN t_cage_position p
ON r.cage_id = p.cage_id
WHERE p.base_id = #{baseId}
AND p.shack = #{dovecoteNumber}

SELECT

FROM t_cage_real r LEFT JOIN t_cage_position p
ON r.cage_id = p.cage_id
WHERE p.base_id = #{baseId}
AND p.shack = #{dovecoteNumber}
AND r.state = 0

SELECT

FROM t_cage_real r LEFT JOIN t_cage_position p
ON r.cage_id = p.cage_id
WHERE p.base_id = #{baseId}
AND p.shack = #{dovecoteNumber}
AND r.state != 0 AND r.state != 5

SELECT

FROM t_cage_real r LEFT JOIN t_cage_position p
ON r.cage_id = p.cage_id
WHERE p.base_id = #{baseId}
AND p.shack = #{dovecoteNumber}
AND r.state = 5

SELECT

FROM t_cage_real r LEFT JOIN t_cage_position p
ON r.cage_id = p.cage_id
WHERE p.base_id = #{baseId}
AND p.shack = #{dovecoteNumber}
AND r.state = #{state}
AND is_deleted = 0
